var limpiarDatosCategorias=function(data,accion){
	var obj=[];
  if(accion==1){
    for(var i=0;i<data.length;i++){
      
      if(data[i].estado==true){
        //Se requiren el objeto completo
        obj.push(data[i]);
      }
    }
  }else{
    for(var i=0;i<data.length;i++){
      if(data[i].destacar==true && data[i].estado==true){
        //Se requiren solo los id's
        obj.push(data[i].id);
      }
    }
  }
	return obj;
}




 
